import { auth } from "@clerk/nextjs/server";
import { prisma } from "@/lib/prismadb";

export async function getOrCreateUserProfile() {
  const { userId } = auth();

  if (!userId) {
    throw new Error("Unauthorized");
  }

  // Check if user profile already exists
  let userProfile = await prisma.userProfile.findUnique({
    where: { userId }
  });

  // Create profile if it doesn't exist
  if (!userProfile) {
    userProfile = await prisma.userProfile.create({
      data: {
        userId,
        proteinGoalDaily: 150, // Default protein goal
        activityLevel: "moderate",
        shareDataForResearch: false,
        allowNotifications: true
      }
    });
  }

  return userProfile;
}

export async function updateUserProfile(data: {
  proteinGoalDaily?: number;
  calorieGoalDaily?: number;
  weight?: number;
  height?: number;
  activityLevel?: string;
  dietaryRestrictions?: string;
  allergies?: string;
}) {
  const { userId } = auth();

  if (!userId) {
    throw new Error("Unauthorized");
  }

  return await prisma.userProfile.update({
    where: { userId },
    data
  });
}

export async function getUserDailyStats(date = new Date()) {
  const { userId } = auth();

  if (!userId) {
    throw new Error("Unauthorized");
  }

  const startOfDay = new Date(date);
  startOfDay.setHours(0, 0, 0, 0);

  const endOfDay = new Date(date);
  endOfDay.setHours(23, 59, 59, 999);

  // Get user profile and today's food logs
  const [userProfile, foodLogs] = await Promise.all([
    prisma.userProfile.findUnique({
      where: { userId }
    }),
    prisma.foodLog.findMany({
      where: {
        userId,
        consumedAt: {
          gte: startOfDay,
          lte: endOfDay
        }
      },
      include: {
        foodItem: true
      }
    })
  ]);

  if (!userProfile) {
    throw new Error("User profile not found");
  }

  // Calculate daily totals
  const dailyTotals = foodLogs.reduce((totals, log) => {
    return {
      calories: totals.calories + log.calories,
      protein: totals.protein + log.protein,
      carbohydrates: totals.carbohydrates + log.carbohydrates,
      fat: totals.fat + log.fat
    };
  }, {
    calories: 0,
    protein: 0,
    carbohydrates: 0,
    fat: 0
  });

  return {
    userProfile,
    dailyTotals,
    foodLogs,
    proteinGoal: userProfile.proteinGoalDaily || 150,
    calorieGoal: userProfile.calorieGoalDaily || 2000
  };
}

import { auth } from "@clerk/nextjs/server";
import { NextResponse } from "next/server";
import { prisma } from "@/lib/prismadb";
import { getOrCreateUserProfile } from "@/lib/user-profile";

export async function POST(req: Request) {
  try {
    const { userId } = auth();
    if (!userId) {
      return new NextResponse("Unauthorized", { status: 401 });
    }

    // Ensure user profile exists
    await getOrCreateUserProfile();

    const body = await req.json();
    const { 
      foodItemId, 
      quantity, 
      unit = "g", 
      mealType, 
      consumedAt 
    } = body;

    if (!foodItemId || !quantity || !mealType) {
      return new NextResponse("Missing required fields", { status: 400 });
    }

    // Get the food item to calculate nutrition
    const foodItem = await prisma.foodItem.findUnique({
      where: { id: foodItemId }
    });

    if (!foodItem) {
      return new NextResponse("Food item not found", { status: 404 });
    }

    // Calculate nutrition based on quantity
    const multiplier = quantity / foodItem.servingSize;
    const calculatedNutrition = {
      calories: foodItem.calories * multiplier,
      protein: foodItem.protein * multiplier,
      carbohydrates: foodItem.carbohydrates * multiplier,
      fat: foodItem.fat * multiplier
    };

    // Create food log entry
    const foodLog = await prisma.foodLog.create({
      data: {
        userId,
        foodItemId,
        quantity: parseFloat(quantity),
        unit,
        mealType,
        consumedAt: consumedAt ? new Date(consumedAt) : new Date(),
        calories: calculatedNutrition.calories,
        protein: calculatedNutrition.protein,
        carbohydrates: calculatedNutrition.carbohydrates,
        fat: calculatedNutrition.fat
      },
      include: {
        foodItem: true
      }
    });

    return NextResponse.json(foodLog);
  } catch (error) {
    console.error("[FOOD_LOG_POST]", error);
    return new NextResponse("Internal Error", { status: 500 });
  }
}

export async function GET(req: Request) {
  try {
    const { userId } = auth();
    if (!userId) {
      return new NextResponse("Unauthorized", { status: 401 });
    }

    const { searchParams } = new URL(req.url);
    const date = searchParams.get("date");
    const mealType = searchParams.get("mealType");

    let startDate = new Date();
    let endDate = new Date();

    if (date) {
      startDate = new Date(date);
      endDate = new Date(date);
    }

    startDate.setHours(0, 0, 0, 0);
    endDate.setHours(23, 59, 59, 999);

    const whereClause: any = {
      userId,
      consumedAt: {
        gte: startDate,
        lte: endDate
      }
    };

    if (mealType) {
      whereClause.mealType = mealType;
    }

    const foodLogs = await prisma.foodLog.findMany({
      where: whereClause,
      include: {
        foodItem: true
      },
      orderBy: {
        consumedAt: "desc"
      }
    });

    return NextResponse.json(foodLogs);
  } catch (error) {
    console.error("[FOOD_LOG_GET]", error);
    return new NextResponse("Internal Error", { status: 500 });
  }
}

export async function DELETE(req: Request) {
  try {
    const { userId } = auth();
    if (!userId) {
      return new NextResponse("Unauthorized", { status: 401 });
    }

    const { searchParams } = new URL(req.url);
    const logId = searchParams.get("id");

    if (!logId) {
      return new NextResponse("Log ID required", { status: 400 });
    }

    // Verify the log belongs to the user
    const foodLog = await prisma.foodLog.findFirst({
      where: {
        id: logId,
        userId
      }
    });

    if (!foodLog) {
      return new NextResponse("Food log not found", { status: 404 });
    }

    await prisma.foodLog.delete({
      where: { id: logId }
    });

    return NextResponse.json({ success: true });
  } catch (error) {
    console.error("[FOOD_LOG_DELETE]", error);
    return new NextResponse("Internal Error", { status: 500 });
  }
}

