tool_input.content:
"use client";

import * as z from "zod";
import { useState, useEffect } from "react";
import { useRouter } from "next/navigation";
import { Plus, Search, Clock, Zap } from "lucide-react";
import { toast } from 'sonner'
import { useForm } from "react-hook-form";

import { zodResolver } from "@hookform/resolvers/zod";
import { Form, FormControl, FormField, FormItem, FormLabel } from "@/components/ui/form";
import Heading from "@/components/heading";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";

import { Empty } from "@/components/empty";
import { Loader } from "@/components/loader";

const quickAddSchema = z.object({
  quantity: z.number().min(0.1, "Quantity must be greater than 0"),
  mealType: z.string().min(1, "Meal type is required"),
});

interface FoodItem {
  id: string;
  name: string;
  brand?: string;
  category: string;
  calories: number;
  protein: number;
  carbohydrates: number;
  fat: number;
  servingSize: number;
  servingUnit: string;
  verified: boolean;
}

interface RecentFood {
  id: string;
  foodItem: FoodItem;
  quantity: number;
  mealType: string;
  consumedAt: string;
}

export default function QuickAddPage() {
  const router = useRouter();
  const [foodItems, setFoodItems] = useState<FoodItem[]>([]);
  const [searchResults, setSearchResults] = useState<FoodItem[]>([]);
  const [recentFoods, setRecentFoods] = useState<RecentFood[]>([]);
  const [selectedFood, setSelectedFood] = useState<FoodItem | null>(null);
  const [searchTerm, setSearchTerm] = useState("");
  const [loading, setLoading] = useState(true);
  const [searching, setSearching] = useState(false);

  const form = useForm<z.infer<typeof quickAddSchema>>({
    resolver: zodResolver(quickAddSchema),
    defaultValues: {
      quantity: 100,
      mealType: getCurrentMealType(),
    },
  });

  useEffect(() => {
    fetchFoodItems();
    fetchRecentFoods();
  }, []);

  useEffect(() => {
    if (searchTerm.length > 2) {
      handleSearch(searchTerm);
    } else {
      setSearchResults([]);
    }
  }, [searchTerm]);

  function getCurrentMealType() {
    const hour = new Date().getHours();
    if (hour < 11) return "breakfast";
    if (hour < 15) return "lunch";
    if (hour < 19) return "dinner";
    return "snack";
  }

  const fetchFoodItems = async () => {
    try {
      // For demo purposes, use the seeded data
      const mockFoodItems: FoodItem[] = [
        {
          id: "1",
          name: "Chicken Breast",
          category: "protein",
          brand: "Generic",
          calories: 165,
          protein: 31,
          carbohydrates: 0,
          fat: 3.6,
          servingSize: 100,
          servingUnit: "g",
          verified: true
        },
        {
          id: "2",
          name: "Greek Yogurt",
          category: "dairy",
          brand: "Generic",
          calories: 100,
          protein: 10,
          carbohydrates: 6,
          fat: 5,
          servingSize: 100,
          servingUnit: "g",
          verified: true
        },
        {
          id: "3",
          name: "Eggs",
          category: "protein",
          brand: "Generic",
          calories: 155,
          protein: 13,
          carbohydrates: 1.1,
          fat: 11,
          servingSize: 100,
          servingUnit: "g",
          verified: true
        },
        {
          id: "4",
          name: "Whey Protein Powder",
          category: "supplements",
          brand: "Generic",
          calories: 103,
          protein: 20,
          carbohydrates: 2,
          fat: 1,
          servingSize: 25,
          servingUnit: "g",
          verified: true
        }
      ];
      setFoodItems(mockFoodItems);
    } catch (error) {
      console.error('Error fetching food items:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchRecentFoods = async () => {
    try {
      // Mock recent foods based on current time
      const mockRecentFoods: RecentFood[] = [
        {
          id: "recent_1",
          foodItem: {
            id: "1",
            name: "Chicken Breast",
            category: "protein",
            brand: "Generic",
            calories: 165,
            protein: 31,
            carbohydrates: 0,
            fat: 3.6,
            servingSize: 100,
            servingUnit: "g",
            verified: true
          },
          quantity: 150,
          mealType: "lunch",
          consumedAt: new Date(Date.now() - 2 * 60 * 60 * 1000).toISOString()
        }
      ];
      setRecentFoods(mockRecentFoods);
    } catch (error) {
      console.error('Error fetching recent foods:', error);
    }
  };

  const handleSearch = async (term: string) => {
    setSearching(true);
    try {
      const filtered = foodItems.filter(food =>
        food.name.toLowerCase().includes(term.toLowerCase()) ||
        food.category.toLowerCase().includes(term.toLowerCase())
      );
      setSearchResults(filtered);
    } catch (error) {
      console.error('Error searching foods:', error);
    } finally {
      setSearching(false);
    }
  };

  const selectFood = (food: FoodItem) => {
    setSelectedFood(food);
    setSearchTerm("");
    setSearchResults([]);
  };

  const addRecentFood = (recentFood: RecentFood) => {
    setSelectedFood(recentFood.foodItem);
    form.setValue("quantity", recentFood.quantity);
    form.setValue("mealType", recentFood.mealType);
  };

  const onSubmit = async (values: z.infer<typeof quickAddSchema>) => {
    if (!selectedFood) {
      toast.error("Please select a food item");
      return;
    }

    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1000));

      toast.success(`Added ${values.quantity}g of ${selectedFood.name} to ${values.mealType}!`);

      // Reset form
      form.reset({
        quantity: 100,
        mealType: getCurrentMealType(),
      });
      setSelectedFood(null);

      // Redirect to dashboard
      router.push('/dashboard');
    } catch (error) {
      toast.error("Failed to add food. Please try again.");
    }
  };

  const mealTypes = [
    { value: "breakfast", label: "🍳 Breakfast" },
    { value: "lunch", label: "🥗 Lunch" },
    { value: "dinner", label: "🍽️ Dinner" },
    { value: "snack", label: "🍎 Snack" },
  ];

  const getCategoryIcon = (category: string) => {
    const icons: { [key: string]: string } = {
      protein: "🥩",
      dairy: "🥛",
      supplements: "💊",
      grains: "🌾",
      vegetables: "🥬",
      fruits: "🍎",
      nuts: "🥜"
    };
    return icons[category] || "🍽️";
  };

  const calculatedNutrition = selectedFood && form.watch("quantity") ? {
    calories: (selectedFood.calories * form.watch("quantity")) / selectedFood.servingSize,
    protein: (selectedFood.protein * form.watch("quantity")) / selectedFood.servingSize,
    carbohydrates: (selectedFood.carbohydrates * form.watch("quantity")) / selectedFood.servingSize,
    fat: (selectedFood.fat * form.watch("quantity")) / selectedFood.servingSize,
  } : null;

  return (
    <div className="max-w-2xl mx-auto">
      <Heading
        title="Quick Add Food"
        description="Quickly add food to your daily log"
        icon={Zap}
        iconColor="text-blue-600"
        bgColor="bg-blue-600/10"
      />

      <div className="px-4 lg:px-8 space-y-6">
        {/* Search Bar */}
        <Card>
          <CardContent className="p-4">
            <div className="relative">
              <Search className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
              <Input
                placeholder="Search for foods..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="pl-9"
              />
            </div>

            {/* Search Results */}
            {searchResults.length > 0 && (
              <div className="mt-4 space-y-2">
                <h4 className="font-medium text-sm text-muted-foreground">Search Results</h4>
                {searchResults.map((food) => (
                  <div
                    key={food.id}
                    onClick={() => selectFood(food)}
                    className="flex items-center justify-between p-3 rounded-lg border hover:bg-muted/50 cursor-pointer"
                  >
                    <div className="flex items-center gap-3">
                      <span className="text-xl">{getCategoryIcon(food.category)}</span>
                      <div>
                        <p className="font-medium">{food.name}</p>
                        <p className="text-xs text-muted-foreground">
                          {food.protein}g protein • {food.calories} cal
                        </p>
                      </div>
                    </div>
                    {food.verified && (
                      <Badge variant="secondary" className="text-xs">
                        Verified
                      </Badge>
                    )}
                  </div>
                ))}
              </div>
            )}

            {searching && (
              <div className="mt-4 flex items-center justify-center">
                <Loader />
              </div>
            )}
          </CardContent>
        </Card>

        {/* Recent Foods */}
        {recentFoods.length > 0 && (
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2 text-base">
                <Clock className="h-4 w-4" />
                Recent Foods
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-2">
              {recentFoods.map((recent) => (
                <div
                  key={recent.id}
                  onClick={() => addRecentFood(recent)}
                  className="flex items-center justify-between p-3 rounded-lg border hover:bg-muted/50 cursor-pointer"
                >
                  <div className="flex items-center gap-3">
                    <span className="text-xl">{getCategoryIcon(recent.foodItem.category)}</span>
                    <div>
                      <p className="font-medium">{recent.foodItem.name}</p>
                      <p className="text-xs text-muted-foreground">
                        {recent.quantity}g • {recent.mealType}
                      </p>
                    </div>
                  </div>
                  <Plus className="h-4 w-4 text-muted-foreground" />
                </div>
              ))}
            </CardContent>
          </Card>
        )}

        {/* Selected Food & Add Form */}
        {selectedFood && (
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <span className="text-xl">{getCategoryIcon(selectedFood.category)}</span>
                {selectedFood.name}
              </CardTitle>
            </CardHeader>
            <CardContent>
              <Form {...form}>
                <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <FormField
                      name="quantity"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Quantity (g)</FormLabel>
                          <FormControl>
                            <Input
                              type="number"
                              step="0.1"
                              min="0.1"
                              placeholder="100"
                              {...field}
                              onChange={(e) => field.onChange(parseFloat(e.target.value))}
                            />
                          </FormControl>
                        </FormItem>
                      )}
                    />

                    <FormField
                      name="mealType"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Meal</FormLabel>
                          <FormControl>
                            <Select onValueChange={field.onChange} value={field.value}>
                              <SelectTrigger>
                                <SelectValue />
                              </SelectTrigger>
                              <SelectContent>
                                {mealTypes.map((meal) => (
                                  <SelectItem key={meal.value} value={meal.value}>
                                    {meal.label}
                                  </SelectItem>
                                ))}
                              </SelectContent>
                            </Select>
                          </FormControl>
                        </FormItem>
                      )}
                    />
                  </div>

                  {/* Nutrition Preview */}
                  {calculatedNutrition && (
                    <div className="p-4 bg-muted rounded-lg">
                      <h4 className="font-semibold mb-3 text-center">Nutrition Preview</h4>
                      <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-center">
                        <div>
                          <p className="text-2xl font-bold text-orange-600">
                            {calculatedNutrition.calories.toFixed(0)}
                          </p>
                          <p className="text-xs text-muted-foreground">Calories</p>
                        </div>
                        <div>
                          <p className="text-2xl font-bold text-blue-600">
                            {calculatedNutrition.protein.toFixed(1)}g
                          </p>
                          <p className="text-xs text-muted-foreground">Protein</p>
                        </div>
                        <div>
                          <p className="text-2xl font-bold text-green-600">
                            {calculatedNutrition.carbohydrates.toFixed(1)}g
                          </p>
                          <p className="text-xs text-muted-foreground">Carbs</p>
                        </div>
                        <div>
                          <p className="text-2xl font-bold text-purple-600">
                            {calculatedNutrition.fat.toFixed(1)}g
                          </p>
                          <p className="text-xs text-muted-foreground">Fat</p>
                        </div>
                      </div>
                    </div>
                  )}

                  <Button 
                    type="submit" 
                    className="w-full" 
                    size="lg"
                    disabled={form.formState.isSubmitting}
                  >
                    {form.formState.isSubmitting ? (
                      <>
                        <Loader className="mr-2 h-4 w-4" />
                        Adding...
                      </>
                    ) : (
                      <>
                        <Plus className="mr-2 h-4 w-4" />
                        Add to {form.watch("mealType")}
                      </>
                    )}
                  </Button>
                </form>
              </Form>
            </CardContent>
          </Card>
        )}

        {/* Popular Foods */}
        {!selectedFood && searchTerm === "" && (
          <Card>
            <CardHeader>
              <CardTitle className="text-base">Popular High-Protein Foods</CardTitle>
            </CardHeader>
            <CardContent>
              {loading ? (
                <div className="flex items-center justify-center py-8">
                  <Loader />
                </div>
              ) : (
                <div className="space-y-2">
                  {foodItems.slice(0, 4).map((food) => (
                    <div
                      key={food.id}
                      onClick={() => selectFood(food)}
                      className="flex items-center justify-between p-3 rounded-lg border hover:bg-muted/50 cursor-pointer"
                    >
                      <div className="flex items-center gap-3">
                        <span className="text-xl">{getCategoryIcon(food.category)}</span>
                        <div>
                          <p className="font-medium">{food.name}</p>
                          <p className="text-xs text-muted-foreground">
                            {food.protein}g protein per {food.servingSize}g
                          </p>
                        </div>
                      </div>
                      <div className="text-right">
                        <p className="text-sm font-semibold text-blue-600">
                          {food.protein}g
                        </p>
                        <p className="text-xs text-muted-foreground">protein</p>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </CardContent>
          </Card>
        )}
      </div>
    </div>
  );
}

import { useState, useEffect, useMemo } from 'react';

interface FoodItem {
  id: string;
  name: string;
  brand?: string;
  category: string;
  calories: number;
  protein: number;
  carbohydrates: number;
  fat: number;
  servingSize: number;
  servingUnit: string;
  verified: boolean;
}

interface SearchFilters {
  category?: string;
  minProtein?: number;
  maxCalories?: number;
  verified?: boolean;
}

export function useFoodSearch(foods: FoodItem[]) {
  const [searchTerm, setSearchTerm] = useState('');
  const [filters, setFilters] = useState<SearchFilters>({});
  const [sortBy, setSortBy] = useState<'name' | 'protein' | 'calories'>('name');

  const filteredAndSortedFoods = useMemo(() => {
    let filtered = foods;

    // Text search
    if (searchTerm.trim()) {
      const search = searchTerm.toLowerCase();
      filtered = filtered.filter(food =>
        food.name.toLowerCase().includes(search) ||
        food.brand?.toLowerCase().includes(search) ||
        food.category.toLowerCase().includes(search)
      );
    }

    // Apply filters
    if (filters.category) {
      filtered = filtered.filter(food => food.category === filters.category);
    }

    if (filters.minProtein !== undefined) {
      filtered = filtered.filter(food => food.protein >= filters.minProtein!);
    }

    if (filters.maxCalories !== undefined) {
      filtered = filtered.filter(food => food.calories <= filters.maxCalories!);
    }

    if (filters.verified !== undefined) {
      filtered = filtered.filter(food => food.verified === filters.verified);
    }

    // Sort results
    filtered.sort((a, b) => {
      switch (sortBy) {
        case 'protein':
          return b.protein - a.protein; // Highest protein first
        case 'calories':
          return a.calories - b.calories; // Lowest calories first
        case 'name':
        default:
          return a.name.localeCompare(b.name);
      }
    });

    return filtered;
  }, [foods, searchTerm, filters, sortBy]);

  const getCategories = useMemo(() => {
    const categories = new Set(foods.map(food => food.category));
    return Array.from(categories).sort();
  }, [foods]);

  const getHighProteinFoods = useMemo(() => {
    return foods
      .filter(food => food.protein >= 15) // High protein threshold
      .sort((a, b) => b.protein - a.protein)
      .slice(0, 10);
  }, [foods]);

  const getLowCalorieFoods = useMemo(() => {
    return foods
      .filter(food => food.calories <= 100 && food.protein >= 10)
      .sort((a, b) => a.calories - b.calories)
      .slice(0, 10);
  }, [foods]);

  const clearFilters = () => {
    setFilters({});
    setSearchTerm('');
  };

  return {
    searchTerm,
    setSearchTerm,
    filters,
    setFilters,
    sortBy,
    setSortBy,
    filteredAndSortedFoods,
    getCategories,
    getHighProteinFoods,
    getLowCalorieFoods,
    clearFilters,
    hasActiveFilters: searchTerm.trim() !== '' || Object.keys(filters).length > 0,
  };
}

import { cn } from "@/lib/utils";

interface DataPoint {
  date: string;
  value: number;
  goal?: number;
}

interface SimpleChartProps {
  data: DataPoint[];
  height?: number;
  className?: string;
  color?: string;
  goalColor?: string;
}

export function SimpleChart({ 
  data, 
  height = 200, 
  className,
  color = "bg-blue-500",
  goalColor = "border-red-400"
}: SimpleChartProps) {
  if (data.length === 0) {
    return (
      <div className={cn("flex items-center justify-center text-muted-foreground", className)} style={{ height }}>
        No data available
      </div>
    );
  }

  const maxValue = Math.max(...data.map(d => Math.max(d.value, d.goal || 0)));
  const minValue = 0;
  const range = maxValue - minValue;

  return (
    <div className={cn("relative", className)} style={{ height }}>
      {/* Chart area */}
      <div className="flex items-end justify-between h-full px-2 pb-8">
        {data.map((point, index) => {
          const barHeight = range > 0 ? ((point.value - minValue) / range) * (height - 40) : 0;
          const goalHeight = point.goal && range > 0 ? ((point.goal - minValue) / range) * (height - 40) : 0;

          return (
            <div key={index} className="relative flex-1 flex flex-col items-center">
              {/* Goal line */}
              {point.goal && goalHeight > 0 && (
                <div 
                  className={cn("absolute w-full border-t-2 border-dashed", goalColor)}
                  style={{ bottom: `${goalHeight + 32}px` }}
                />
              )}

              {/* Bar */}
              <div className="relative w-full max-w-8 mx-1">
                <div
                  className={cn("w-full rounded-t-sm transition-all duration-300", color)}
                  style={{ height: `${Math.max(barHeight, 2)}px` }}
                />

                {/* Value label */}
                <div className="absolute -top-6 left-1/2 transform -translate-x-1/2 text-xs font-medium">
                  {Math.round(point.value)}
                </div>
              </div>

              {/* Date label */}
              <div className="absolute -bottom-6 left-1/2 transform -translate-x-1/2 text-xs text-muted-foreground whitespace-nowrap">
                {point.date}
              </div>
            </div>
          );
        })}
      </div>

      {/* Legend */}
      <div className="absolute bottom-0 left-0 right-0 flex justify-center gap-4 text-xs">
        <div className="flex items-center gap-1">
          <div className={cn("w-3 h-3 rounded", color)} />
          <span>Actual</span>
        </div>
        {data.some(d => d.goal) && (
          <div className="flex items-center gap-1">
            <div className={cn("w-3 h-1 border-t-2 border-dashed", goalColor)} />
            <span>Goal</span>
          </div>
        )}
      </div>
    </div>
  );
}

interface ProgressRingProps {
  value: number;
  max: number;
  size?: number;
  strokeWidth?: number;
  className?: string;
  showValue?: boolean;
  unit?: string;
  color?: string;
}

export function ProgressRing({
  value,
  max,
  size = 120,
  strokeWidth = 8,
  className,
  showValue = true,
  unit = "",
  color = "text-blue-500"
}: ProgressRingProps) {
  const radius = (size - strokeWidth) / 2;
  const circumference = radius * 2 * Math.PI;
  const progress = Math.min((value / max) * 100, 100);
  const strokeDashoffset = circumference - (progress / 100) * circumference;

  return (
    <div className={cn("relative", className)} style={{ width: size, height: size }}>
      <svg
        width={size}
        height={size}
        className="transform -rotate-90"
      >
        {/* Background circle */}
        <circle
          cx={size / 2}
          cy={size / 2}
          r={radius}
          stroke="currentColor"
          strokeWidth={strokeWidth}
          fill="transparent"
          className="text-muted/20"
        />

        {/* Progress circle */}
        <circle
          cx={size / 2}
          cy={size / 2}
          r={radius}
          stroke="currentColor"
          strokeWidth={strokeWidth}
          fill="transparent"
          strokeDasharray={circumference}
          strokeDashoffset={strokeDashoffset}
          strokeLinecap="round"
          className={cn("transition-all duration-500", color)}
        />
      </svg>

      {showValue && (
        <div className="absolute inset-0 flex flex-col items-center justify-center">
          <span className="text-2xl font-bold">
            {Math.round(value)}{unit}
          </span>
          <span className="text-sm text-muted-foreground">
            of {max}{unit}
          </span>
        </div>
      )}
    </div>
  );
}

